import { useState } from "react";
import quizzes from "./bitcoin_quizzes_49_topics.json";
import "./App.css";

export default function App() {
  const [activeLesson, setActiveLesson] = useState(0);
  const [completed, setCompleted] = useState([]);

  return (
    <div className="app purple">
      <h1>Bitcoin Institute</h1>
      <div className="lesson-list">
        {quizzes.map((q, i) => {
          const locked = i > 0 && !completed.includes(i - 1);
          return (
            <button
              key={i}
              disabled={locked}
              onClick={() => setActiveLesson(i)}
            >
              Lesson {i + 1}: {q.title} {locked ? "üîí" : ""}
            </button>
          );
        })}
      </div>
      <Lesson
        lesson={quizzes[activeLesson]}
        index={activeLesson}
        onPass={() => setCompleted([...completed, activeLesson])}
      />
    </div>
  );
}

function Lesson({ lesson, index, onPass }) {
  return (
    <div className="lesson">
      <h2>{lesson.title}</h2>
      <div className="video">VIDEO PLACEHOLDER</div>
      <Quiz quiz={lesson} onPass={onPass} />
    </div>
  );
}

function Quiz({ quiz, onPass }) {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(null);

  const handleSelect = (questionIndex, optionIndex) => {
    if (submitted) return;
    setAnswers(prev => ({ ...prev, [questionIndex]: optionIndex }));
  };

  const submitQuiz = () => {
    let correct = 0;

    quiz.questions.forEach((q, i) => {
      if (answers[i] === q.correctIndex) correct++;
    });

    const percent = Math.round(
      (correct / quiz.questions.length) * 100
    );

    setScore(percent);
    setSubmitted(true);

    if (percent >= 70) {
      onPass();
    }
  };

  return (
    <div className="quiz">
      <h3>Lesson Quiz</h3>

      {quiz.questions.map((q, i) => (
        <div key={i} className="quiz-question">
          <p><strong>{i + 1}. {q.question}</strong></p>

          {q.options.map((opt, idx) => (
            <label key={idx} className="quiz-option">
              <input
                type="radio"
                name={`question-${i}`}
                checked={answers[i] === idx}
                onChange={() => handleSelect(i, idx)}
                disabled={submitted}
              />
              {opt}
            </label>
          ))}
        </div>
      ))}

      {!submitted && (
        <button
          className="btn"
          onClick={submitQuiz}
          disabled={Object.keys(answers).length !== quiz.questions.length}
        >
          Submit Quiz
        </button>
      )}

      {submitted && (
        <div className="quiz-result">
          <h4>Your Score: {score}%</h4>
          {score >= 70 ? (
            <p className="pass">‚úÖ Passed ‚Äî next lesson unlocked</p>
          ) : (
            <p className="fail">‚ùå Not passed ‚Äî review and try again</p>
          )}
        </div>
      )}
    </div>
  );
}

