import { useState } from "react";
import quizzes from "./bitcoin_quizzes_49_topics.json";
import "./App.css";

export default function App() {
  const [activeLesson, setActiveLesson] = useState(0);
  const [completed, setCompleted] = useState([]);

  return (
    <div className="app purple">
      <h1>Bitcoin Institute</h1>
      <div className="lesson-list">
        {quizzes.map((q, i) => {
          const locked = i > 0 && !completed.includes(i - 1);
          return (
            <button
              key={i}
              disabled={locked}
              onClick={() => setActiveLesson(i)}
            >
              Lesson {i + 1}: {q.title} {locked ? "ðŸ”’" : ""}
            </button>
          );
        })}
      </div>
      <Lesson
        lesson={quizzes[activeLesson]}
        index={activeLesson}
        onPass={() => setCompleted([...completed, activeLesson])}
      />
    </div>
  );
}

function Lesson({ lesson, index, onPass }) {
  return (
    <div className="lesson">
      <h2>{lesson.title}</h2>
      <div className="video">VIDEO PLACEHOLDER</div>
      <Quiz quiz={lesson} onPass={onPass} />
    </div>
  );
}

function Quiz({ quiz, onPass }) {
  const [answers, setAnswers] = useState({});
  const [submitted, setSubmitted] = useState(false);
  const [score, setScore] = useState(0);

  const submit = () => {
    let correct = 0;
    quiz.questions.forEach((q, i) => {
      if (answers[i] === q.correctIndex) correct++;
    });
    const pct = Math.round((correct / quiz.questions.length) * 100);
    setScore(pct);
    setSubmitted(true);
    if (pct >= 70) onPass();
  };

  return (
    <div className="quiz">
      {quiz.questions.map((q, i) => (
        <div key={i}>
          <p>{q.question}</p>
          {q.options.map((o, j) => (
            <label key={j}>
              <input type="radio" name={i} onChange={() => setAnswers({...answers, [i]: j})} />
              {o}
            </label>
          ))}
        </div>
      ))}
      {!submitted && <button onClick={submit}>Submit</button>}
      {submitted && <p>Score: {score}%</p>}
    </div>
  );
}

